import React from 'react';
import { withRouter } from 'react-router-dom';
import styled from 'styled-components';
import links from '../../links'

//////
const LandingPageWrapper = styled.div`
`;

const DXDLogo = styled.img`
    margin-bottom: 8px;
`;

const DXDSection = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 124px;
`;

const BannerSection = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    margin-top: 84px;
`;

const TagLine = styled.div`
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: 600;
    font-size: 12px;
    line-height: 15px;
    text-align: center;
    text-transform: uppercase;
    color: var(--light-header-text);

    text-overflow: ellipsis;
    overflow: hidden;
    white-space: pre;


`;

const BannerMessage = styled.div`
    font-family: Raleway;
    font-style: normal;
    font-weight: 400;
    font-size: 44px;
    line-height: 52px;
    text-align: center;
    color: var(--dark-text);
    margin-top: 12px;

    text-overflow: ellipsis;
    overflow: hidden;
    white-space: pre;
`;

const HeavyMessage = styled.span`
    font-weight: 600;
`;

const Description = styled.div`
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: 200;
    font-size: 16px;
    line-height: 31px;
    text-align: center;
    letter-spacing: 0.03em;
    color: var(--light-body-text);
    margin-top: 24px;
    max-width: 614px;

`;

const ButtonRow = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    margin-top: 32px;
`;

const Button = styled.div`
    display: flex;
    justify-content: center;
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 18px;
    display: flex;
    align-items: center;
    text-align: center;
    letter-spacing: 0.02em;
    width: 120px;
    height: 43px;
    color: var(--dark-text);
    border: 1px solid rgba(51, 51, 51, 0.2);
    border-radius: 3px;
    margin: 0px 10px;
    &:hover {
        border: 1px solid var(--lp-border-color-onHover);
        cursor: pointer;
    }
`;

const DarkButton = styled(Button)`
    background: var(--dark-button-background);
    border: 1px solid rgba(0, 0, 0, 0.075);
    box-sizing: border-box;
    border-radius: 3px;
    font-weight: bold;
    color: var(--white);
    &:hover {
        background: #1799d9;
        border: 1px solid #158ec9;
        cursor: pointer;
    }
`;

///////

const ProductSection = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-top: 124px;
`;

const Message = styled.div`
    font-family: Raleway;
    font-style: normal;
    font-weight: 600;
    font-size: 24px;
    line-height: 28px;
    text-align: center;
    letter-spacing: 0.02em;
    color: var(--dark-text);
    margin-top: 16px;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: pre;
`;

const ProductPanelWrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    // margin-top: 18px;

    /*       top right booton left*/
    padding: 0px 0px 0px 00px;
    margin: 18px 0px 0px 0px;


`;

const ProductClickable = styled.a`
    display: block;
    text-decoration: none;
`;

const ProductPanel = styled.div`
    display: flex;
    flex-direction: column;
    background: var(--white);
    border: 1px solid rgba(51, 51, 51, 0.2);
    border-radius: 3px;
    margin: 0px 10px;
    height: 198px;
    width: 456px;
    padding: 32px;
    &:hover {
        border: 1px solid var(--lp-border-color-onHover);
    }
`;

const ProductNameWrapper = styled.div`
    display: flex;
`;

const ProductLogo = styled.img``;

const ProductName = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: Raleway;
    font-style: normal;
    font-weight: 600;
    font-size: 22px;
    line-height: 26px;
    margin-left: 16px;
    letter-spacing: 0.02em;
    color: var(--dark-text);
`;

const ProductDescription = styled.div`
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: normal;
    font-size: 14px;
    line-height: 27px;
    letter-spacing: 0.03em;
    margin-top: 32px;
    color: var(--light-body-text);
`;

///////

const AboutUsSection = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-top: 124px;
`;

const AboutPanelWrapper = styled.div`
    display: flex;
    flex-direction: row;
    margin-top: 64px;
`;

const AboutPanel = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: start;
    width: 25%;
    margin: 0px 22px;
`;

const AboutIcon = styled.img``;

const AboutHeader = styled.div`
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: bold;
    font-size: 16px;
    line-height: 28px;
    letter-spacing: 0.03em;
    text-align: center;
    margin-top: 32px;
`;

const AboutDescription = styled.div`
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 28px;
    text-align: center;
    color: var(--light-body-text);
    margin-top: 24px;
`;

///////

const JoinSection = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 124px 0px;
`;

const JoinActionWrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 32px;

`;

const JoinAction = styled.a`
    display: flex;
    flex-direction: row;
    font-family: Source Sans Pro;
    font-style: normal;
    font-weight: bold;
    font-size: 18px;
    line-height: 32px;
    align-items: center;
    letter-spacing: 0.02em;
    color: #189ddf;
    margin-top: 8px;
    text-decoration: none;
    &:hover {
        color: #1690cc;
    }
`;

const ReadAction = styled(JoinAction)`
    margin: 0;
`;

const JoinActionText = styled.div``;

const JoinActionArrow = styled.img`
    margin-left: 12px;
`;

////////

const LandingPage = () => {
    const BlueButton = withRouter(
        ({ option, route, history, location, children }) => {
            return (
                <DarkButton
                    onClick={() => {
                        history.push(route);
                    }}
                >
                    {children}
                </DarkButton>
            );
        }
    );

    const NormalButton = withRouter(
        ({ option, route, history, location, children }) => {
            return (
                <Button
                    onClick={() => {
                        history.push(route);
                    }}
                >
                    {children}
                </Button>
            );
        }
    );

    return (

        <LandingPageWrapper>
                <BannerSection>
                        <TagLine>We decentralize DeFi</TagLine>
                    <BannerMessage>
                        <HeavyMessage>DXdao </HeavyMessage>is a{' '}
                        <HeavyMessage>sovereign collective</HeavyMessage>
                    </BannerMessage>
                    <Description>
                        The DXdao is a decentralized organization initialized in May
                        of 2019 and has over 400 unique stakeholder addresses. It
                        develops, governs, and grows DeFi protocols and products.
                        Owned and operated by the community, the DXdao has the
                        potential to significantly scale its membership.
                    </Description>
                    <ButtonRow>
                            <BlueButton route={links.landing_invest}>Invest</BlueButton>
                            <NormalButton route={links.landing_faq}>FAQ</NormalButton>
                            <Button
                                onClick={() => {
                                    window.open(
                                        `${links.landing_whitepaper}`,
                                        '_blank'
                                    );
                                }}
                            >
                                Whitepaper
                            </Button>
                    </ButtonRow>
                </BannerSection>
                <DXDSection>
                    <DXDLogo src="DXD.svg" />
                    <Message>DXD powers the Decentralised Ecosystem</Message>
                    <Description margin="12px">
                        The DXD token is DXdao´s native token. DXD token holders
                        have an economic claim to the DXdao´s revenue. It also
                        grants its owners future access to a suite of services and
                        premium features in decentralised applications, such as
                        gasless transactions, feeless anonymizing of assets,
                        reduced trading fees on DEX protocols and more.
                    </Description>
                </DXDSection>
                <ProductSection>
                    <TagLine>Our collectively owned products</TagLine>
                    <Message>Pure Dapps powered by DXdao</Message>
                    <ProductPanelWrapper>
                        <ProductClickable
                            href={links.landing_mixeth}
                            target="_blank"
                        >
                            <ProductPanel>
                                <ProductNameWrapper>
                                    <ProductLogo src="Mix.svg" />
                                    <ProductName>Mix.eth</ProductName>
                                </ProductNameWrapper>
                                <ProductDescription>
                                    Mix is a portfolio manager for the Ethereum
                                    ecosystem with privacy, security, and a good
                                    user experience as core features.
                                </ProductDescription>
                            </ProductPanel>
                        </ProductClickable>
                        <ProductClickable
                            href={links.landing_omeneth}
                            target="_blank"
                        >
                            <ProductPanel>
                                <ProductNameWrapper>
                                    <ProductLogo src="Omen.svg" />
                                    <ProductName>Omen.eth</ProductName>
                                </ProductNameWrapper>
                                <ProductDescription>
                                    Omen is a fully decentralized prediction market
                                    platform built on top of the Gnosis conditional
                                    token framework.
                                </ProductDescription>
                            </ProductPanel>
                        </ProductClickable>
                    </ProductPanelWrapper>
                    <ProductPanelWrapper>
                        <ProductClickable
                            href={links.landing_mesaeth}
                            target="_blank"
                        >
                            <ProductPanel>
                                <ProductNameWrapper>
                                    <ProductLogo src="Mesa.svg" />
                                    <ProductName>Mesa.eth</ProductName>
                                </ProductNameWrapper>
                                <ProductDescription>
                                    Mesa is an Open Source interface for the Gnosis
                                    Protocol, a fully permissionless DEX that
                                    enables ring trades to maximize liquidity.
                                </ProductDescription>
                            </ProductPanel>
                        </ProductClickable>
                        <ProductClickable
                            href={links.landing_dxswapeth}
                            target="_blank"
                        >
                            <ProductPanel>
                                <ProductNameWrapper>
                                    <ProductLogo src="Dxswap.svg" />
                                    <ProductName>DXswap.eth</ProductName>
                                </ProductNameWrapper>
                                <ProductDescription>
                                    DXswap is a protocol for exchanging ERC-20 
                                    tokens.
                                </ProductDescription>
                            </ProductPanel>
                        </ProductClickable>
                    </ProductPanelWrapper>
                </ProductSection>
                <AboutUsSection>
                    <TagLine>About us</TagLine>
                    <Message>The first super-scalable collective</Message>
                    <AboutPanelWrapper>
                        <AboutPanel>
                            <AboutIcon src="No-Hierarchy.svg" />
                            <AboutHeader>No Hierarchies</AboutHeader>
                            <AboutDescription>
                                There is no boss. Members find consensus through
                                voting.
                            </AboutDescription>
                        </AboutPanel>
                        <AboutPanel>
                            <AboutIcon src="Symmetry.svg" />
                            <AboutHeader>Symmetry of Information</AboutHeader>
                            <AboutDescription>
                                Equal access to information for all members.
                            </AboutDescription>
                        </AboutPanel>
                        <AboutPanel>
                            <AboutIcon src="Swarm.svg" />
                            <AboutHeader>Swarm Knowledge</AboutHeader>
                            <AboutDescription>
                                The transparent feedback system enables decision
                                making with a high degree of success.
                            </AboutDescription>
                        </AboutPanel>
                        <AboutPanel>
                            <AboutIcon src="Open.svg" />
                            <AboutHeader>Open for Everyone</AboutHeader>
                            <AboutDescription>
                                Permissionless access increases the knowledge and
                                effectiveness of the collective.
                            </AboutDescription>
                        </AboutPanel>
                    </AboutPanelWrapper>
                </AboutUsSection>
                <JoinSection>
                <Message>Join now</Message>
                <JoinActionWrapper>
                    <JoinAction
                        href={links.landing_create_proposal}
                        target="_blank"
                    >
                        <JoinActionText>Create a proposal</JoinActionText>
                        <JoinActionArrow src="RightArrow.svg" />
                    </JoinAction>
                    <JoinAction
                        href={links.landing_start_forum_discussion}
                        target="_blank"
                    >
                        <JoinActionText>
                            Start a forum discussion
                        </JoinActionText>
                        <JoinActionArrow src="RightArrow.svg" />
                    </JoinAction>
                    <JoinAction 
                        href={links.landing_community_chat} 
                        target="_blank">
                        <JoinActionText>Community chat</JoinActionText>
                        <JoinActionArrow src="RightArrow.svg" />
                    </JoinAction>
                    <JoinAction
                        href={links.landing_developer_chat}
                        target="_blank"
                    >
                        <JoinActionText>Developer chat</JoinActionText>
                        <JoinActionArrow src="RightArrow.svg" />
                    </JoinAction>
                    <JoinAction
                        href={links.landing_newsletter}
                        target="_blank"
                    >

                    </JoinAction>
                </JoinActionWrapper>
            </JoinSection>
        </LandingPageWrapper>
    );
};

export default LandingPage;
